import * as types from '~/reducers/index.type';
export const changeReward = (index, rewardName) => {
  return {
    type: types.CHANGE_REWARD,
    payload: {
      index,
      rewardName
    }
  };
};
export const changeLadderQty = ladderQty => {
  return {
    type: types.CHANGE_LADDER_QTY,
    payload: {
      ladderQty
    }
  };
};
export const changeName = (index, playerName) => {
  return {
    type: types.CHANGE_NAME,
    payload: {
      index,
      playerName
    }
  };
};
export const resetAll = () => {
  return {
    type: types.RESET_ALL,
    payload: {}
  };
};
